import React, { Component } from "react";
import getEmployees from "../utils/API";
import Jumbotron from "./Jumbotron";
import { MDBDataTableV5 } from 'mdbreact';

class EmployeeContainer extends Component {
    state = {
        employees: [],
        info: []
    };


    componentDidMount() {
        this.loadInitialEmployees();
    }

    loadInitialEmployees = () => [
        getEmployees()
            .then(res => res.data.results)
            .then(data => {
                this.setState({ employees: data })
            })
            .then(async () => {
                this.setState({ info: this.employeeInfo(), isLoading: false })
                    .catch((err) => {
                        console.log(err);
                    })
            })
    ];

    // filterEmployees = () => {
    //     // go through array of current employees with a filter, and if they match filter keep them in array
    //     const filteredEmployees = this.state.employees.filter(employee => {
    //         if (employee.name == this.state.searchFilter) {
    //             return true;
    //         }
    //     });
    //     console.log("searching for employee with exact match of ", this.state.searchFilter);
    //     console.log("new filtered list is ", filteredEmployees)

    //     this.setState({
    //         employees: filteredEmployees
    //     });
    // }

    employeeInfo = () => {
        let employees = this.state.employees.map((employee) => {
            return (
                {
                    photo: <img
                        src={employee.picture.large}
                        alt='employee'
                    />,
                    firstNam

                }
            )
        })
    }


    render() {

        // this.state.employees.map(employee => {

        // });

        const datatable = {
            columns: [
                {
                    label: 'picture',
                    field: 'photo',
                    sort: 'disabled',
                    width: 200

                },
                {
                    label: 'Name',
                    field: 'name',
                    width: 150,
                    attributes: {
                        'aria-controls': 'DataTable',
                        'aria-label': 'Name',
                    },
                },
                {
                    label: 'City',
                    field: 'City',
                    width: 200,
                },
                {
                    label: 'email',
                    field: 'email',
                    width: 200,
                },
                {
                    label: 'phone',
                    field: 'phone',
                    width: 200,
                },
                {
                    label: 'cell',
                    field: 'cell',
                    width: 200,
                }
            ],
            rows: this.state.employees
        }


        return (
            < MDBDataTableV5 hover
                entriesOptions={[10, 25]}
                entries={10}
                pagesAmount={5}
                data={datatable}
                pagingTop
                searchTop
                searchBottom={false}
            />
        );

    }
}

export default EmployeeContainer;
